import React from 'react'
import { ImageBackground, StyleSheet, View, Dimensions,Text, Alert } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import logoAPP from '../assets/logo.png'

export default function SplashScreen() {

    const edges = useSafeAreaInsets();
    const bgColor = '#FAFAFA';
    const textColor = '#757575';
    const footerText='Developed By Vinuri Hemalka';

    const styles = StyleSheet.create({
        container: {
            flex: 1,
            justifyContent: "center",
            alignItems: 'center'
        },
        image: {
            flex: 1,
            width: Dimensions.get('window').width * 0.8,
        },
        bgcontainer:{
            position: 'absolute',
            top: 0,
            bottom: 0,
            left: 0,
            right: 0,
            backgroundColor: bgColor
        },
        textstyle:{
            bottom:20.0,
            color:textColor
        }
    });

    setTimeout(() => {  
        navigation.push('Home');
     }, 5000);

    return (


        <View style={styles.bgcontainer}>

            <View style={styles.container}>
                <ImageBackground source={logoAPP} resizeMode="contain" style={styles.image}>
                </ImageBackground>

                <Text style={styles.textstyle}>{footerText}</Text>
            </View>

        </View>
    );


}
